import React, { useState, useEffect } from 'react';
import '../styles/Experience.css'; // Adjust the path as needed
const fullExperienceText =
  "From Neopets avatar customization to building real-world tech, a B.S. in Software Engineering and minor in Digital Technology fueled my passion to bridge the gap between code and design. My internship journey began with building a health check monitor for microservices at Eagleview. At Liberty Mutual, I merged code and design by crafting a landing page and icons for their Mainframe team. Chase Cost Management saw me tackle user interfaces and build a client portal to help users visualize their savings data. Poatek IT Consulting honed my skills in automating project tracking with APIs, and finally, at Inward, I streamlined data analysis, ensuring valuable insights were readily available.";

const companyPattern = /([A-Z][a-z]+(?: [A-Z][a-z]+)*)/g; // Matches company names with word breaks

function Experience() {
  const [typedText, setTypedText] = useState('');
  const [cursorVisible, setCursorVisible] = useState(false);

  const typingSpeed = 100; // Adjust typing speed in milliseconds

  useEffect(() => {
    let index = 0;
    let currentText = '';

    const type = () => {
      currentText = fullExperienceText.substring(0, index + 1);
      // Replace company names with clickable links
      currentText = currentText.replace(companyPattern, (match) => {
        const companyName = match.trim();
        const url = `https://www.${companyName.toLowerCase()}.com/`; // Replace with your logic
        return `<a href="${url}">${companyName}</a>`;
      });
      setTypedText(currentText);
      index++;
      if (index > fullExperienceText.length) {
        clearInterval(intervalId);
        setCursorVisible(true);
      }
    };

    const intervalId = setInterval(type, typingSpeed);

    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setCursorVisible((prevVisible) => !prevVisible);
    }, 500); // Adjust blinking speed in milliseconds

    return () => clearInterval(cursorInterval);
  }, []);

  return (
    <div className="experience-page">
      <h2>EXPERIENCE</h2>
      <p className="experience-text" dangerouslySetInnerHTML={{ __html: typedText }}></p>
    </div>
  );
}

export default Experience;
