import React, { useState, useEffect } from 'react';
import '../styles/Experience.css'; // Adjust the path as needed
const fullExperienceText = `
  From Neopets webpage customization to building real-world tech, a B.S. in Software Engineering and minor in Digital Technology fueled my passion to bridge the gap between code and design. My internship journey began with building a health check monitor for microservices at Eagleview. At Liberty Mutual, I developed a landing page and icons for their Mainframe team. Chase Cost Management allowed me to build a client portal to help users visualize their savings data. Poatek IT Consulting honed my skills in automating project tracking with APIs, and finally, at Inward, I streamlined data analysis, ensuring valuable insights were readily available.
`;

const Experience = () => {
  const [textVisible, setTextVisible] = useState(false);

  useEffect(() => {
    const timeoutId = setTimeout(() => setTextVisible(true), 500); // Adjust fade-in delay

    return () => clearTimeout(timeoutId);
  }, []);

  const withCompanyLinks = fullExperienceText.replace(
    /Neopets|Eagleview|Liberty Mutual|Chase Cost Management|Poatek IT Consulting|Inward/g,
    (match) => {
      switch (match) {
        case 'Neopets':
          return `<u href="https://www.neopets.com/"target="_blank">Neopets</u>`;
        case 'Eagleview':
          return `<a href="https://www.eagleview.com/"target="_blank">Eagleview</a>`;
        case 'Liberty Mutual':
          return `<a href="https://www.libertymutual.com/"target="_blank">Liberty Mutual</a>`;
        case 'Chase Cost Management':
          return `<a href="https://www.ccmchase.com/"target="_blank">Chase Cost Management</a>`;
        case 'Poatek IT Consulting':
          return `<a href="https://www.poatek.com/"target="_blank">Poatek IT Consulting</a>`;
        case 'Inward':
          return `<a href="https://www.joininward.com/"target="_blank">Inward</a>`;
        default:
          return match; // Handle unexpected matches (optional)
      }
    }
  );

  return (
    <div className="experience-page">
      <p className={`experience-text ${textVisible ? 'visible' : ''}`} dangerouslySetInnerHTML={{ __html: withCompanyLinks }}></p>
    </div>
  );
};

export default Experience;
