import React from 'react';

const ExperienceItem = ({ title, company, date, responsibilities }) => {
  // Function to format company name and link (assuming company has a website property)
  const formatCompanyName = (companyName, website) => {
    if (!website) return companyName; // Handle missing website

    return (
      <a href={website} target="_blank" rel="noreferrer">
        {companyName}
      </a>
    );
  };

  return (
    <div className="experience-item">
      <h3>{title}</h3>
      <h4>{company} - {date}</h4>
      <ul>
        {responsibilities.map((responsibility, index) => (
          <li key={index}>{responsibility}</li>
        ))}
      </ul>
    </div>
  );
};

export default ExperienceItem;
