import React, { useState, useEffect } from 'react';
import '.styles/Experience.css'; // Adjust the path as needed

const experiences =
  "From Neopets avatar customization to building real-world tech, a B.S. in Software Engineering and minor in Digital Technology fueled my passion to explore the design side of code. My internships started with building a health check monitor for microservices at Eagleview (https://www.eagleview.com/). At Liberty Mutual (https://www.libertymutual.com/), I merged code and design by crafting a landing page and icons for their Mainframe team. Chase Cost Management (https://www.chasecostmanagement.com/) saw me tackle user interfaces and build a client portal. Poatek IT Consulting (https://www.poatek.com/) had me automating project tracking with APIs, and finally, at Inward (https://www.inwardhq.com/), I streamlined data analysis. Now, I'm ready to leverage this diverse experience to create dope, user-friendly tech solutions.";

const Experience = () => {
  const [text, setText] = useState('');
  const [cursorVisible, setCursorVisible] = useState(false);

  const typingSpeed = 100; // Adjust typing speed in milliseconds

  useEffect(() => {
    let index = 0;
    let currentText = '';

    const type = () => {
      currentText = experiences.substring(0, index + 1);
      setText(currentText);
      index++;
      if (index > experiences.length) {
        clearInterval(intervalId);
        setCursorVisible(true);
      }
    };

    const intervalId = setInterval(type, typingSpeed);

    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setCursorVisible((prevVisible) => !prevVisible);
    }, 500); // Adjust blinking speed in milliseconds

    return () => clearInterval(cursorInterval);
  }, []);

  return (
    <div className="experience-container">
      <h2>EXPERIENCE</h2>
      <p className="experience-text">
        {text}
        <span className={`cursor ${cursorVisible ? 'visible' : 'hidden'}`}>|</span>
      </p>
    </div>
  );
};

export default Experience;
