import React, { useState, useRef } from 'react';
import './MusicPlayer.css';

const MusicPlayer = ({ audioUrl, songTitle, artist }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  const togglePlay = () => {
    setIsPlaying(!isPlaying);

    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play().catch(error => {
          // Handle any play error, e.g., autoplay not allowed by browser
          console.error('Error playing audio:', error);
        });
      }
    }
  };

  return (
    <div className="music-player">
      <button className={`play-button ${isPlaying ? 'playing' : ''}`} onClick={togglePlay}>
        <span className="play-icon">{isPlaying ? 'Pause' : 'Play'}</span>
      </button>
      <div className="song-info">
        <div className="song-title">{songTitle}</div>
        <div className="artist">{artist}</div>
      </div>
      <audio
        ref={audioRef}
        src={audioUrl}
        onCanPlay={() => {
          if (isPlaying) {
            audioRef.current.play().catch(error => {
              // Handle any play error, e.g., autoplay not allowed by browser
              console.error('Error playing audio:', error);
            });
          }
        }}
      />
    </div>
  );
};

export default MusicPlayer;
