import React from 'react';
import './HeroSection.css';

const HeroSection = () => {
  return (
    <section className="hero-section">
      <h1 className="headline">Welcome to Your Portfolio</h1>
      <p className="subheadline">Showcasing Your Awesome Work</p>
      <button className="cta-button">Get Started</button>
    </section>
  );
};

const heroSection = document.querySelector('.hero-section');
const beforeElement = heroSection.querySelector('::before');

let posX = 0;
const speed = 0.1; // Adjust the speed of the wave

function updatePosition() {
  posX += speed;
  beforeElement.style.backgroundPositionX = `${posX}%`;
  requestAnimationFrame(updatePosition);
}

updatePosition();


export default HeroSection;
