import React, { useEffect, useState } from 'react';
import '../styles/HeroSection.css';

const HeroSection = () => {
  const [text, setText] = useState('Dahlia Xie');
  const sentences = [
    ' is a Software Engineer based out of Seattle, Washington.',
    'Showcasing Your Awesome Work',
    'Get Started'
  ];
  const speed = 100; // Typing speed in milliseconds

  useEffect(() => {
    let index = 0;
    let currentText = 'Dahlia Xie';
    let isDeleting = false;

    const type = () => {
      const currentSentence = sentences[index % sentences.length];
      if (!isDeleting) {
        currentText = currentSentence.substring(0, currentText.length + 1);
        setText(currentText);
        if (currentText === currentSentence) {
          isDeleting = true;
          setTimeout(type, 2000); // Wait before deleting
        }
      } else {
        currentText = currentSentence.substring(0, currentText.length - 1);
        setText(currentText);
        if (currentText === '') {
          isDeleting = false;
          index++;
        }
      }
      setTimeout(type, speed);
    };

    type();

    return () => clearTimeout(type);
  }, []);

  return (
    <section className="hero-section">
      <h1 className="headline">
        <span className="name">Dahlia Xie</span>{text}
      </h1>
    </section>
  );
};

export default HeroSection;
