import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom
import '../styles/HeroSection.css';

const HeroSection = () => {
  const [text, setText] = useState('');
  const [showCursor, setShowCursor] = useState(false);
  const [typewriterFinished, setTypewriterFinished] = useState(false); // Track if typewriter effect has finished
  const name = 'Dahlia Xie';
  const sentence = 'is a Software Engineer based out of Seattle, Washington';
  const typingSpeed = 100; // Typing speed in milliseconds
  const history = useHistory(); // Initialize useHistory

  useEffect(() => {
    if (!typewriterFinished) { // Check if typewriter effect hasn't finished yet
      let index = 0;
      let currentText = '';

      const type = () => {
        currentText = sentence.substring(0, index + 1);
        setText(currentText);
        index++;
        if (index > sentence.length) {
          clearInterval(intervalId);
          setShowCursor(true);
          setTypewriterFinished(true); // Set typewriterFinished to true when typewriter effect finishes
        }
      };

      const intervalId = setInterval(type, typingSpeed);

      return () => clearInterval(intervalId);
    }
  }, [typewriterFinished]); // Run effect when typewriterFinished changes

  useEffect(() => {
    if (!typewriterFinished) { // Check if typewriter effect hasn't finished yet
      const cursorInterval = setInterval(() => {
        setShowCursor(prevState => !prevState);
      }, 500); // Blinking speed in milliseconds

      return () => clearInterval(cursorInterval);
    }
  }, [typewriterFinished]); // Run effect when typewriterFinished changes

  useEffect(() => {
    const unlisten = history.listen(() => {
      setTypewriterFinished(false); // Reset typewriterFinished when navigating away from home page
    });

    return () => unlisten();
  }, [history]);

  return (
    <section className="hero-section">
      <h1 className="headline">
        <span className="name">{name}</span> {text}
        <span style={{ visibility: showCursor ? 'visible' : 'hidden' }}>|</span>
      </h1>
    </section>
  );
};

export default HeroSection;
