import React, { useState, useEffect } from 'react';
import './MusicPlayer.css';

const MusicPlayer = ({ audioUrl, songTitle, artist }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioContext = new (window.AudioContext || window.webkitAudioContext)();
  let audioBuffer = null;
  let audioSource = null;

  useEffect(() => {
    // Load the audio file
    fetch(audioUrl)
      .then(response => response.arrayBuffer())
      .then(data => audioContext.decodeAudioData(data))
      .then(decodedData => {
        audioBuffer = decodedData;
      })
      .catch(error => {
        console.error('Error loading audio:', error);
      });
  }, [audioUrl]);

  const togglePlay = () => {
    if (isPlaying) {
      if (audioSource && (audioSource.playbackState === audioSource.PLAYING_STATE || audioSource.playbackState === audioSource.SCHEDULED_STATE)) {
        audioSource.stop();
      }
    } else {
      if (audioBuffer) {
        audioSource = audioContext.createBufferSource(); // Create a new AudioBufferSourceNode
        audioSource.buffer = audioBuffer;
        audioSource.connect(audioContext.destination);
        audioSource.start();
      }
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="music-player">
      <button className={`play-button ${isPlaying ? 'playing' : ''}`} onClick={togglePlay}>
        <span className="play-icon">&#9654;</span>
      </button>
      <div className="song-info">
        <div className="song-title">{songTitle}</div>
        <div className="artist">{artist}</div>
      </div>
    </div>
  );
};

export default MusicPlayer;
