import React, { useState } from 'react';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });
  const [isSubmitted, setIsSubmitted] = useState(false); // State to track form submission

  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = (event) => {
    fetch('/', { // Update the endpoint to match your server
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
    .then((response) => {
      if (response.ok) {
        console.log('Form data successfully posted to the endpoint:', formData);
        setFormData({ name: '', email: '', message: '' });
        setIsSubmitted(true); // Set the state to true after form submission
      } else {
        throw new Error('Failed to post form data to the endpoint');
      }
    })
    .catch((error) => {
      console.error('Error posting form data:', error);
    });
  };
  return (
    <div>
      {!isSubmitted ? ( // Render the form if not submitted
        <form onSubmit={handleSubmit} className="contact-form" name="contactform"method="POST" data-netlify="true">
          <input type="hidden" name="form-name" value="contact" /> {/* Hidden form name field */}
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />

          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />

          <label htmlFor="message">Message:</label>
          <textarea
            id="message"
            name="message"
            value={formData.message}
            onChange={handleChange}
            required
          />

          <button type="submit">Send Message</button>
        </form>
      ) : (
        // Render the confirmation message if submitted
        <p>Thank you for your message! I will get back to you soon.</p>
      )}
    </div>
  );
};

export default ContactForm;
