import React, { useEffect, useState } from 'react';
import '../styles/HeroSection.css';

const HeroSection = () => {
  const [text, setText] = useState('');
  const [showCursor, setShowCursor] = useState(false);
  const [typewriterFinished, setTypewriterFinished] = useState(false);
  const name = 'Dahlia Xie';
  const sentence = 'is a Software Engineer based out of Seattle, Washington';
  const typingSpeed = 100;

  useEffect(() => {
    const hasTypewriterPlayed = sessionStorage.getItem('hasTypewriterPlayed');
    if (hasTypewriterPlayed) {
      setTypewriterFinished(true);
    } else {
      let index = 0;
      const intervalId = setInterval(() => {
        setText(prevText => prevText + sentence[index]);
        index++;
        if (index === sentence.length) {
          clearInterval(intervalId);
          setShowCursor(true);
          sessionStorage.setItem('hasTypewriterPlayed', 'true');
        }
      }, typingSpeed);

      return () => clearInterval(intervalId);
    }
  }, []);

  useEffect(() => {
    if (typewriterFinished) {
      const cursorInterval = setInterval(() => {
        setShowCursor(prevState => !prevState);
      }, 500);

      return () => clearInterval(cursorInterval);
    }
  }, [typewriterFinished]);

  return (
    <section className="hero-section">
      <h1 className="headline">
        <span className="name">{name}</span> {sentence}
        <span style={{ visibility: showCursor ? 'visible' : 'hidden' }}>|</span>
      </h1>
    </section>
  );
};

export default HeroSection;
