import React, { useEffect, useState } from 'react';
import '../styles/HeroSection.css';

const HeroSection = () => {
  const [text, setText] = useState('');
  const sentences = [
    'a Software Engineer',
    'a Baker',
    'a Runner',
    // Add more sentences here as needed
  ];
  const name = 'Dahlia Xie';
  const speed = 100; // Typing speed in milliseconds

  useEffect(() => {
    let index = 0;
    let currentText = '';
    let isDeleting = false;

    const type = () => {
      const currentSentence = sentences[index % sentences.length];
      if (!isDeleting) {
        currentText = `${name} is ${currentSentence.substring(0, currentText.length + 1)}`;
        setText(currentText);
        if (currentText === `${name} is ${currentSentence}`) {
          isDeleting = true;
          setTimeout(type, 2000); // Wait before deleting
        }
      } else {
        currentText = `${name} is ${currentSentence.substring(0, currentText.length - 1)}`;
        setText(currentText);
        if (currentText === `${name} is `) {
          isDeleting = false;
          index++;
        }
      }
      setTimeout(type, speed);
    };

    type();

    return () => clearTimeout(type);
  }, []);

  return (
    <section className="hero-section">
      <h1 className="headline">
        {text}
      </h1>
    </section>
  );
};

export default HeroSection;
