import React from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import MusicPlayer from '../components/MusicPlayer';
import HeroSection from '../components/HeroSection';
import VisitorCounter from '../components/VisitorCounter'; // Assuming you uncommented this component

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Home = () => {
  return (
    <Router>
      <Container>
        {/* Add your content here */}
        <HeroSection />
        <Switch>
          <Route path="/about">
            {/* Render about section */}
          </Route>
          <Route path="/portfolio">
            {/* Render portfolio section */}
            
          </Route>
          <Route path="/contact">
            {/* Render contact form */}
            
          </Route>
          <Route path="/">
            {/* Render homepage content */}
            {/* Conditionally render the VisitorCounter component */}
            <VisitorCounter />
            {/* Conditionally render the MusicPlayer component */}
            <MusicPlayer
              audioUrl="/song.mp3"
              songTitle="Heaven Can Wait"
              artist="Organ Tapes"
            />
          </Route>
        </Switch>
      </Container>
    </Router>
  );
};

export default Home;
