import React from 'react';
import './MusicPlayer.css'; // You can define your own CSS styles

const MusicPlayer = ({ title, artist, isPlaying, onTogglePlay, playlistUrl }) => {
  return (
    <div className="UI_player__Uxw70">
      <div className="PlaybackControls_playbackControls__Js7Pj">
        <button
          type="button"
          className={`PlaybackControls_left__5EVB0 PlaybackControls_btn__MCppa ${
            isPlaying ? 'PlaybackControls_btnHover__fnnO_' : ''
          }`}
          onClick={onTogglePlay}
          tabIndex="1"
          aria-label={`playing ${title} by ${artist} - click to ${isPlaying ? 'stop' : 'start'} playback`}
        >
          {/* Playback icons */}
        </button>
        <a
          className="PlaybackControls_right__4kW_r PlaybackControls_btn__MCppa"
          href={playlistUrl}
          rel="noopener noreferrer"
          target="_blank"
          tabIndex="2"
          aria-label="Open Playlist in TIDAL"
        >
          {/* TIDAL logo */}
        </a>
      </div>
      <div className="PlaybackControls_trackInfo__x7mjE">
        <div className="PlaybackControls_title__6506m">{title}</div>
        <div className="PlaybackControls_artist__o68Dc">{artist}</div>
      </div>
    </div>
  );
};

export default MusicPlayer;
