import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import HeroSection from './components/HeroSection';
import FooterBanner from './components/FooterBanner'; // Import the FooterBanner component
import MusicPlayer from './components/MusicPlayer';
import './App.css';

const App = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };
  return (
    <Router>
      <div className="app">
        <Header />
        <Routes>
          <Route path="/" element={<HeroSection />} />
          {/* Add more routes for other pages */}
        </Routes>
        <MusicPlayer
        title="Feeling"
        artist="LADIPOE, Buju"
        isPlaying={isPlaying}
        onTogglePlay={togglePlay}
        playlistUrl="https://tidal.com/browse/playlist/3d95c4f6-dad5-4d7f-a469-8bde01b7771d"
      />

        <FooterBanner /> {/* Include the FooterBanner component */}
      </div>
    </Router>
  );
};

export default App;
