import React, { useState, useEffect } from 'react';

const ExperienceItem = ({ experiences }) => {
  // Similar to HeroSection, manage text and cursor state
  const [text, setText] = useState('');
  const [showCursor, setShowCursor] = useState(false);

  // Define typing speed and blinking speed
  const typingSpeed = 100;
  const cursorBlinkSpeed = 500;

  useEffect(() => {
    let currentText = '';
    let index = 0;

    const typeWriter = () => {
      currentText = experiences.text.substring(0, index + 1);
      setText(currentText);
      index++;
      if (index > experiences.text.length) {
        clearInterval(intervalId);
        setShowCursor(true);
      }
    };

    const intervalId = setInterval(typeWriter, typingSpeed);

    return () => clearInterval(intervalId);
  }, [experiences]);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setShowCursor(prevState => !prevState);
    }, cursorBlinkSpeed);

    return () => clearInterval(cursorInterval);
  }, []);

  // Function to format company name and link
  const formatCompanyName = (companyName, website) => {
    return (
      <a href={website} target="_blank" rel="noreferrer">
        {companyName}
      </a>
    );
  };

  return (
    <div className="experience-item">
      <h3>{experiences.title}</h3>
      <h4>{experiences.date}</h4>
      <p>
        {experiences.text.split('\n').map((line, index) => (
          <span key={index}>
            {line}
            <br />
          </span>
        ))}
      </p>
    </div>
  );
};

export default ExperienceItem;
