import React, { useEffect, useState } from 'react';
import '../styles/HeroSection.css';

const HeroSection = () => {
  const [text, setText] = useState('');
  const name = 'Dahlia Xie';
  const sentences = [
    'is a Software Engineer based out of Seattle, Washington.',
    'Showcasing Your Awesome Work',
    'Get Started'
  ];
  const speed = 100; // Typing speed in milliseconds

  useEffect(() => {
    let index = 0;
    let currentText = '';
    let isDeleting = false;
    let timeoutId; // Variable to hold the timeout ID

    const type = () => {
      const currentSentence = sentences[index % sentences.length];
      if (!isDeleting) {
        currentText = currentSentence.substring(0, currentText.length + 1);
        setText(currentText);
        if (currentText === currentSentence) {
          isDeleting = true;
          timeoutId = setTimeout(type, 2000); // Wait before deleting
        }
      } else {
        if (!currentText.includes(name)) {
          currentText = currentSentence.substring(0, currentText.length - 1);
          setText(currentText);
          if (currentText === '') {
            isDeleting = false;
            index++;
          }
        }
        timeoutId = setTimeout(type, speed); // Set timeout for deletion
      }
    };

    type();

    // Clear timeout when component unmounts or when type function changes
    return () => clearTimeout(timeoutId);
  }, []); // Empty dependency array ensures useEffect runs only once

  return (
    <section className="hero-section">
      <h1 className="headline">
        <span className="name">{name}</span> {text}
      </h1>
    </section>
  );
};

export default HeroSection;
